import React, { useState, useEffect } from 'react';
import {firestore} from './firebase/firebase'; // Import the Firestore instance
import {collection, query, where, getDocs, updateDoc, doc, setDoc, getDoc} from 'firebase/firestore';


function App() {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRef = collection(firestore,'users').doc('rSB9tOxXbfDiLcX1aUzy'); // Replace with your document ID
        const userDoc = await userRef.get();
        if (userDoc.exists) {
          setUserData(userDoc.data());
        } else {
          console.log('No such document!');
        }
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>User Data:</h1>
      {userData ? (
        <pre>{JSON.stringify(userData, null, 2)}</pre>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
