import React, { useEffect, useState } from 'react';
import {db} from '../../firebase/firebase';
import {collection, query, where, getDocs, updateDoc, doc, setDoc, getDoc, addDoc} from 'firebase/firestore';
import FloatingComponent from '../floating/floating';
import Dialog from '@mui/material/Dialog';
import './display.css';
import { exchangeCodeForToken, refreshToken } from '../../spotify/auth/auth'; // Import token handling functions

function Display(props) {
    //Dialog
    const [selectedData, setSelectedData] = useState(null);
    const [open, setOpen] = useState(false);

    //tokens
    const [accessToken, setAccessToken] = useState(null);
    const [expiresIn, setExpiresIn] = useState(null);
    const [refreshToken, setRefreshToken] = useState(null);
    const {authCode} = props;


    //Userdata cleanup
    const[user,setUser] = useState(null);
    const[song, setSong] = useState(null);



    //userdata

    const [userList, setUserList] = useState([]);


    //updating song when user changes
    const [countdown, setCountdown] = useState(15);
    const [first, setFirst] = useState(false);

      //clock for refreshing userSong
  useEffect(()=>{
    const interval = setInterval(() => {
      if(countdown>0){
        setCountdown(countdown-1);
      }
      else{
        clearInterval(interval);
      }
    },1000)
    return () => {clearInterval(interval)}
  },[countdown])



  // Token exchange
  useEffect(() => {
    if (authCode) {
      exchangeCodeForToken(authCode)
        .then((data) => {
          setAccessToken(data.accessToken);
          setExpiresIn(data.expiresIn);
          setRefreshToken(data.refreshToken);
        })
        .catch((error) => {
          console.error('Error exchanging code for access token:', error);
        });
    }
  }, [authCode]);



    // Refreshing accessToken when access expires
    useEffect(() => {
      if (!expiresIn || !refreshToken) return;
      const interval = setInterval(() => {
        refreshToken(refreshToken)
          .then((data) => {
            setAccessToken(data.accessToken);
            setExpiresIn(data.expiresIn);
            setRefreshToken(data.refreshToken);
          })
          .catch((error) => {
            console.error('Error refreshing token:', error);
          });
      }, (expiresIn - 60) * 1000);

      return () => clearInterval(interval);
    }, [expiresIn, refreshToken]);







  //fetching user image and name
  const fetchUserInfo = async (token) => {
    try {
      const response = await fetch('https://api.spotify.com/v1/me', {
        headers: {
          'Authorization': 'Bearer ' + token,
        },
      });
      const userData = response.json();
      return userData;  

    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(()=>{
    if(!accessToken) return;
    const runFetchUserInfo = () => {
      fetchUserInfo(accessToken)
      .then((res) => {
        setUser(res);
      })
    }
    runFetchUserInfo();

  },[accessToken]);


  //fetching songName and preview url
  const fetchNowPlaying = async (token) => {
    try {
      const response = await fetch("https://api.spotify.com/v1/me/player/currently-playing", {
        headers: {
          'Authorization': 'Bearer ' + token,
        },
      });
      const data = await response.json();

      return data;
    } catch (error) {
      console.error('Error:', error);
    }
  }

  useEffect(()=>{
    if(!accessToken) return;
    if(!user) return;
    const func = () => {
      fetchNowPlaying(accessToken)
      .then((res)=>{
        setSong(res);
        const newObj = {
          userSong: res.name,
          userImg: user.images[1].url,
          userName: user.display_name,
          songPreview: res.preview_url
        }
        sendFloatingDataToFirebase(newObj);
      })      
    } 
    if(!first || countdown===0){
      fetchUsers();
      func();
      setFirst(true);
      setCountdown(15);
    }
  },[accessToken, countdown, user])


  //Dialog handlers
  const handleComponentClick = (data) => {
    setSelectedData(data);
    setOpen(true);
  }

  const handleCloseDialog = () => {
    setOpen(false);
    setSelectedData(null);
  }

  //update firebase
  const sendFloatingDataToFirebase = async (data) => {
    try {
      const docRef = await addDoc(collection(db, "users"), data);

    } catch (error) {
      console.error('Error updating data in Firebase:', error);
    }
  };



    //get data from firebase
    const fetchUsers = async () => {
        try {
          const userRef = collection(db, 'users');
          const userQuerySnapshot = await getDocs(userRef);

          const userDataArray = [];
          userQuerySnapshot.forEach((doc) => {
            userDataArray.push(doc.data());
          });

          setUserList(userDataArray);
          console.log(userList);
        } catch (error) {
          console.error('Error fetching data:', error);
        }
      };


  return (
  <div className="background">
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <span></span>
    <div>
      <h1>User Data:</h1>
      {userList.length > 0 ? (
        <div>
          {userList.map((user, index) => (
            <div key={index} className='floating-body'>
              <img alt='profile' src={user.profile_picture} />
              <div className='floating-body-text'>
                <p>{user.username}</p>
                <p>Now playing: {user.song}</p>
                <audio controls={true} autoPlay name="media">
                  <source src={user.preview_url} type="audio/mpeg" />
                </audio>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
    {userList.map((user, index) => (
        <FloatingComponent
          key={index} // Make sure to use a unique key for each component
          data={{
            userSong: user.song,
            userImg: user.profile_picture,
            userName: user.name,
            songPreview: user.profile_picture,
          }}
          onClick={handleComponentClick}
        />
      ))}
    <Dialog
      open={open}
      onClose={handleCloseDialog}
      PaperProps={{
        sx:{
          width:'100vw',
          height:'60svh',
          borderRadius:'25%'
        }
      }}
      >
      <div className='floating-body'>
        <img alt='profile' src={selectedData? selectedData.userImg : ''}/>
        <button onClick={handleCloseDialog}>exit</button>
        <div className='floating-body-text'>
        <p>{selectedData? selectedData.userName : ''}</p>
        <p>Now playing: {selectedData? selectedData.userSong : ''}</p>
        <video controls={true} autoPlay name="media">
          <source src= {selectedData? selectedData.songPreview : ''}type="audio/mpeg"/>
          </video>
          </div>
      </div>
    </Dialog>
  </div>
  );
}

export default Display;
