const authCode = new URLSearchParams(window.location.search).get('code');

function App() {
  return (
    <Router>
      <Routes>
        {authCode ? (
          <Route path="/" element={<Display authCode={authCode} />
        } />
        ) : (
          <Route path="/" element={<Login />} />

        )}
      </Routes>
    </Router>
  );
}

export default App;
