import React, { useState, useEffect } from 'react';
import {firestore} from './firebase/firebase'; // Import the Firestore instance
import {collection, query, where, getDocs, updateDoc, doc, setDoc, getDoc, addDoc} from 'firebase/firestore';


function App() {
  const [userData, setUserData] = useState(null);


  const addDocumentToUsersCollection = async () => {
  try {
        const usersCollection = collection(firestore,'users'); // Reference to the "users" collection

        // Define the data you want to add to the document
        const data = {
          preview_url: 'John Doe',
          profile_picture: 'dfsfdfsdf',
          song: 'adicted to you',
          username: 'Erle Widme'
          // Add more fields as needed
        };

        // Add a new document with a generated id.
        const docRef = await addDoc(collection(firestore, "users"), {
          data
        });

        setUserData(docRef);
        console.log("Document written with ID: ", docRef.id);

      } catch (error) {
        console.error('Error adding document:', error);
      }
    };




  useEffect(() => {
    const fetchData = async () => {
      addDocumentToUsersCollection();
      try {
        const userRef = collection(firestore,'users'); // Replace with your document ID
        const userDoc = await getDocs(userRef);
        console.log("getting from firebase 1 " ,userDoc.docs[0].data())
        console.log("getting from firebase 2: " ,userDoc.docs[1].data())

      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h1>User Data:</h1>
      {userData ? (
        <pre>{JSON.stringify(userData, null, 2)}</pre>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
