import React, { useState, useEffect } from 'react';
import { firestore } from './firebase/firebase'; // Import the Firestore instance
import { collection, getDocs } from 'firebase/firestore';

function App() {
  const [userData, setUserData] = useState(null);

  const addDocumentToUsersCollection = async () => {
    try {
      const usersCollection = collection(firestore, 'users'); // Reference to the "users" collection

      // Define the data you want to add to the document
      const data = {
        preview_url: 'John Doe',
        profile_picture: 'dfsfdfsdf',
        song: 'adicted to you',
        username: 'Erle Widme',
        // Add more fields as needed
      };

      // Add a new document to the "users" collection
      const docRef = await addDoc(usersCollection, data);

      console.log('Document written with ID:', docRef.id);
    } catch (error) {
      console.error('Error adding document:', error);
    }
  };

  const fetchData = async () => {
    try {
      const userRef = collection(firestore, 'users');
      const userQuerySnapshot = await getDocs(userRef);

      const userDataArray = [];
      userQuerySnapshot.forEach((doc) => {
        userDataArray.push(doc.data());
      });

      setUserData(userDataArray);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    // Add a new document when the component first mounts
    addDocumentToUsersCollection();

    // Fetch data from Firestore
    fetchData();
  }, []);

  return (
    <div>
      <h1>User Data:</h1>
      {userData ? (
        <pre>{JSON.stringify(userData, null, 2)}</pre>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default App;
